INSERT INTO `t_template_field_content` VALUES ('2402', '检查方法：钡棉检查。\r\n约位于　处可见钡棉存留，经吞钡后钡棉位置无改变；余食管通畅，无明显狭窄和梗阻，未见龛影和充盈缺损形成；食道粘膜无增粗、紊乱和破坏。', 'US_ExaminetionSee');
