INSERT INTO `t_template_field_content` VALUES ('2403', '检查方法：常规钡透；\r\n常规胸透：两肺野未见明确实变影，心膈未见异常；\r\n食道吞钡：吞钡后食管上段钡剂通过顺利，壁柔软，黏膜皱襞正常；食管下段扩张，食管收缩欠佳，排空稍延迟，黏膜皱襞粗细不均，食管下段及胃底可见串珠状或蚯蚓状充盈缺损，食道壁凹凸不平；其它：未见异常。   ', 'US_ExaminetionSee');
INSERT INTO `t_template_field_content` VALUES ('2404', '', 'US_Advise');
