            this.label16.Location = new System.Drawing.Point(45, 242);
            this.label16.Name = "label16";
            this.label16.Size = new System.Drawing.Size(692, 17);
            this.label16.TabIndex = 10000226;
            this.label16.Text = "输血治疗包括输全血、成分血，是临床治疗的重要措施之一，是临床抢救危重患者生命行之有效的手段。";
            // 
            // groupBox1
            // 
            this.groupBox1.BackColor = System.Drawing.SystemColors.ActiveBorder;
            this.groupBox1.Location = new System.Drawing.Point(7, 223);
            this.groupBox1.Name = "groupBox1";
            this.groupBox1.Size = new System.Drawing.Size(805, 10);
            this.groupBox1.TabIndex = 10000227;
            this.groupBox1.TabStop = false;
            // 
            // label17
            // 
            this.label17.Location = new System.Drawing.Point(45, 266);
            this.label17.Name = "label17";
            this.label17.Size = new System.Drawing.Size(692, 17);
            this.label17.TabIndex = 10000228;
            this.label17.Text = "但输血存在一定风险，可能发生输血反应及感染经血传播疾病。";
            // 
            // label18
            // 
            this.label18.Location = new System.Drawing.Point(45, 288);
            this.label18.Name = "label18";
            this.label18.Size = new System.Drawing.Size(692, 35);
            this.label18.TabIndex = 10000229;
            this.label18.Text = "虽然我院使用的血液，均已按卫生部有关规定进行检测，但由于当前科技水平的限制，输血仍有某些不能预测   或不能防止的输血反应和输血传染病。输血时可能发生的主要情况如" +
                "下：";
            // 
            // label19
            // 
            this.label19.Location = new System.Drawing.Point(76, 324);
            this.label19.Name = "label19";
            this.label19.Size = new System.Drawing.Size(105, 17);
            this.label19.TabIndex = 10000230;
            this.label19.Text = "1、过敏反应";
            // 
            // label20
            // 
            this.label20.Location = new System.Drawing.Point(362, 324);
            this.label20.Name = "label20";
            this.label20.Size = new System.Drawing.Size(105, 17);
            this.label20.TabIndex = 10000231;
            this.label20.Text = "2、发热反应";
            // 
            // label21
            // 
            this.label21.Location = new System.Drawing.Point(76, 346);
            this.label21.Name = "label21";
            this.label21.Size = new System.Drawing.Size(221, 17);
            this.label21.TabIndex = 10000232;
            this.label21.Text = "3、感染肝炎（乙肝、丙肝等）";
            // 
            // label22
            // 
            this.label22.Location = new System.Drawing.Point(362, 346);
            this.label22.Name = "label22";
            this.label22.Size = new System.Drawing.Size(222, 17);
            this.label22.TabIndex = 10000233;
            this.label22.Text = "4、感染爱滋病，梅毒";
            // 
            // label23
            // 
            this.label23.Location = new System.Drawing.Point(76, 366);
            this.label23.Name = "label23";
            this.label23.Size = new System.Drawing.Size(222, 17);
            this.label23.TabIndex = 10000234;
            this.label23.Text = "5、感染疟疾";
            // 
            // label24
            // 
