            this.label24.Name = "label24";
            this.label24.Size = new System.Drawing.Size(222, 17);
            this.label24.TabIndex = 10000235;
            this.label24.Text = "6、巨细胞病毒或EB病毒感染";
            // 
            // label25
            // 
