            this.label26.Name = "label26";
            this.label26.Size = new System.Drawing.Size(568, 18);
            this.label26.TabIndex = 10000237;
            this.label26.Text = "在您及家人或监护人了解上述可能发生的情况后，如同意输血治疗，请在下面签字。";
            // 
            // label27
            // 
            this.label27.Location = new System.Drawing.Point(86, 439);
            this.label27.Name = "label27";
            this.label27.Size = new System.Drawing.Size(222, 17);
            this.label27.TabIndex = 10000238;
            this.label27.Text = "受血者（家人或监护人）签字：";
            // 
            // label28
            // 
